 drop database if exists hometask5;
 create database hometask5;

use hometask5;

drop table if exists users;
create table users(
	id SERIAL primary key,
	name varchar(20),
	created_at datetime,
	updated_at datetime
);


alter table users add birthday timestamp default now();
truncate users;




select count(*) as total, dayname( date_add(birthday , interval year(now()) - year(birthday) year)) as day_of_birth from users
group by dayname( date_add(birthday , interval year(now()) - year(birthday) year));


